mapper.select:
SELECT ITEM_SEQ, CATEGORY_ID, COMPANY_NAME, PILL_NAME, DETAIL_CONTENT, PILL_HOWTO, INTAKE_PRECAUTIONS, ATPNQESITM, INTRCQESITM, SEQESITM, PILL_DEPOSIT, TO_CHAR(OPEN_DATE, 'YYYY-MM-DD') AS OPEN_DATE, TO_CHAR(UPDATE_DATE, 'YYYY-MM-DD') AS UPDATE_DATE, PILL_COLOR, PILL_SHAPE, PILL_IMAGE
FROM (
    SELECT ROWNUM AS RNUM, ITEM_SEQ, CATEGORY_ID, COMPANY_NAME, PILL_NAME, DETAIL_CONTENT, PILL_HOWTO, INTAKE_PRECAUTIONS, ATPNQESITM, INTRCQESITM, SEQESITM, PILL_DEPOSIT, OPEN_DATE, UPDATE_DATE, PILL_COLOR, PILL_SHAPE, PILL_IMAGE
    FROM (
        SELECT ITEM_SEQ, CATEGORY_ID, COMPANY_NAME, PILL_NAME, DETAIL_CONTENT, PILL_HOWTO, INTAKE_PRECAUTIONS, ATPNQESITM, INTRCQESITM, SEQESITM, PILL_DEPOSIT, OPEN_DATE, UPDATE_DATE, PILL_COLOR, PILL_SHAPE, PILL_IMAGE
        FROM TBL_PILL

            WHERE ${keywordType} LIKE '%' || #{keyword} || '%'

        ORDER BY ITEM_SEQ DESC
        )

    WHERE ROWNUM <= #{page} * #{amount}

    )

WHERE RNUM > (#{page} - 1) * #{amount}

mapper.update:
UPDATE TBL_PILL
SET PILL_NAME = #{pillName}, PILL_IMAGE = #{pillImage}
WHERE ITEM_SEQ = #{itemSeq}
